package com.acxie.leetcode.leetcode算法题.至少有K个重复字符的最长子串;

import java.util.HashMap;
import java.util.List;
import java.util.stream.Collectors;

/**
 * @description:暴力
 * @create: 2020/02/22 21:02
 */
public class 至少有K个重复字符的最长子串 {

    public static int longestSubstring(String s, int k) {

        List<Character> list = s.chars().mapToObj(c -> (char) c).collect(Collectors.toList());
        int i = 0;
        int now = 0;
        int maxresult = 0;

        HashMap<Character, Integer> map = new HashMap<Character, Integer>();
        for (int j = 0; j < list.size(); j++) {
            if (list.size() - j <= maxresult) {
                break;
            }
            int max = 0;

            for (int l = j; l < list.size(); l++) {
                if (map.containsKey(list.get(l))) {
                    map.replace(list.get(l), map.get(list.get(l)) + 1);
                } else {
                    map.put(list.get(l), 1);
                }
//                System.out.println(map.values());
                if (l - j + 1 > maxresult && !map.values().stream().filter(a -> a < k).findFirst().isPresent()) {
                    max = l - j + 1;
                    System.out.println(max);
                }
            }
            maxresult = Math.max(max, maxresult);
//            map = new HashMap<Character, Integer>();
            map.replace(list.get(j), map.get(list.get(j)) - 1);
        }
        return maxresult;
    }

    public static void main(String[] args) {
        System.out.println(longestSubstring("", 301));
    }
}
